document.querySelector("#write-btn").addEventListener("click",n=>{const e=JSON.parse(window.localStorage.user).id,t=document.querySelector("#p-title").value,o=document.querySelector("#p-body").value;fetch("/api/posts",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({userId:e,title:t,body:o})}).then(s=>{loader("#content","/components/my-posts.html")})});
